styles:
.alert-container {
  height: 100%;
  padding-bottom: 10px;
  position: relative;
  display: flex;
  flex-direction: column;
}
.alert-container .alert-image {
  flex-grow: 0.7;
  display: flex;
  align-items: center;
  justify-content: center;
}
.alert-container .alert-image img {
  max-width: 80%;
  max-height: 90%;
}
.alert-container .alert-content {
  overflow: auto;
  margin: auto;
  width: fit-content;
  height: fit-content;
  max-height: 100%;
  max-width: 100%;
  padding: 10px;
}

