styles:
.confirm-container {
  height: 100%;
  width: 100%;
  padding-bottom: 44px;
  position: relative;
}
.confirm-container .confirm-content {
  width: 100%;
  height: 100%;
  position: relative;
}
.confirm-container .confirm-content > div {
  position: absolute;
  width: fit-content;
  height: fit-content;
  max-width: 100%;
  max-height: 100%;
  padding: 16px;
  overflow: auto;
  margin: auto;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
}
.confirm-container.content-left .confirm-content > div {
  width: 100%;
  height: auto;
}
.confirm-container .confirm-buttons {
  height: 44px;
  padding: 5px 0;
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
  text-align: center;
}
.confirm-container .confirm-buttons .core-btn {
  margin-left: 10px;
}
.confirm-container .confirm-buttons .core-btn:first-child {
  margin-left: 0;
}
.confirm-container .confirm-buttons.right {
  text-align: right;
  padding-right: 20px;
}

