styles:
.modal {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 999;
  display: block;
}
.modal.transparent {
  pointer-events: none;
}
.modal.transparent .background-overlay {
  display: none;
}
.modal.transparent .container {
  pointer-events: all;
  box-shadow: 0 0 20px #ededed;
}
.modal .background-overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.2);
}
.modal .container {
  position: absolute;
  display: inline-block;
  background-color: #fff;
  margin: auto;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  padding: 0;
  border-radius: 6px;
  overflow: hidden;
}
.modal .container.hasHeader {
  padding-top: 60px;
}
.modal .container .header {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 60px;
  background-color: #EBF0F7;
}
.modal .container .header > h4 {
  user-select: none;
  position: absolute;
  top: 0;
  width: 100%;
  left: 0;
  text-align: center;
  line-height: 60px;
  color: #112F59;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
  margin: 0;
  padding: 0 50px;
  font-size: 20px;
}
.modal .container .header .modal-close {
  width: 20px;
  height: 20px;
  position: absolute;
  right: 10px;
  bottom: 0;
  margin: auto;
  font-family: serif;
  top: 0;
  font-size: 24px;
  color: #eaeaea;
  text-align: center;
  user-select: none;
  cursor: pointer;
}
.modal .container .header .modal-close:hover {
  color: #fff;
}

